mapper.insert:
INSERT INTO sys_role(
id, company_id, code, name, is_locked, remarks, create_by, create_date, update_by, update_date, del_flag,
) VALUES (
#{ id }, #{ company.id }, #{ code }, #{ name }, #{ isLocked }, #{ remarks }, #{ createBy }, #{ createDate }, #{ updateBy }, #{ updateDate }, #{ delFlag },
)

mapper.update:
UPDATE sys_role SET
company_id = #{ company.id }, code = #{ code }, name = #{ name }, is_locked = #{ isLocked }, remarks = #{ remarks }, update_by = #{ updateBy }, update_date = #{ updateDate },
WHERE id = #{id} and update_date = #{oldUpdateDate}

UPDATE sys_role SET
update_by = #{updateBy},
update_date = #{updateDate},
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
and update_date = #{oldUpdateDate}

mapper.insert:
INSERT INTO sys_role(
id, company_id, code, name, is_locked, remarks, create_by, create_date, update_by, update_date, del_flag,
) VALUES
(
#{entity.id}, #{entity.company.id}, #{entity.code}, #{entity.name}, #{entity.isLocked}, #{entity.remarks}, #{entity.createBy}, #{entity.createDate}, #{entity.updateBy}, #{entity.updateDate}, #{entity.delFlag},
)

mapper.update:
UPDATE sys_role



      when id=#{entity.id} then #{entity.company.id}




      when id=#{entity.id} then #{entity.code}




      when id=#{entity.id} then #{entity.name}




      when id=#{entity.id} then #{entity.isLocked}




      when id=#{entity.id} then #{entity.remarks}




      when id=#{entity.id} then #{entity.updateBy}




      when id=#{entity.id} then #{entity.updateDate}



WHERE

  id = #{entity.id} and update_date = #{entity.oldUpdateDate}

UPDATE sys_role



      when id=#{entity.id} then 1




      when id=#{entity.id} then #{entity.updateBy}




      when id=#{entity.id} then #{entity.updateDate}



WHERE

  id = #{entity.id} and update_date = #{entity.oldUpdateDate}
