)

mapper.update:
UPDATE sys_user_role SET
user_id = #{ user.id }, role_id = #{ role.id }, remarks = #{ remarks }, update_by = #{ updateBy }, update_date = #{ updateDate },
WHERE id = #{id}

UPDATE sys_user_role SET
update_by = #{updateBy},
update_date = #{updateDate},
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

