mapper.insert:
INSERT INTO org_department(
id, company_id, code, parent_id, ids, name, sort, director, manager, remarks, create_by, create_date, update_by, update_date, del_flag,
) VALUES (
#{ id }, #{ company.id }, #{ code }, #{ parent.id }, #{ ids }, #{ name }, #{ sort }, #{ director.id }, #{ manager.id }, #{ remarks }, #{ createBy }, #{ createDate }, #{ updateBy }, #{ updateDate }, #{ delFlag },
)

mapper.update:
UPDATE org_department SET
company_id = #{ company.id }, code = #{ code }, parent_id = #{ parent.id }, ids = #{ ids }, name = #{ name }, sort = #{ sort }, director = #{ director.id }, manager = #{ manager.id }, remarks = #{ remarks }, update_by = #{ updateBy }, update_date = #{ updateDate },
WHERE id = #{id} and update_date = #{oldUpdateDate}

UPDATE org_department SET
update_by = #{updateBy},
update_date = #{updateDate},
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
and update_date = #{oldUpdateDate}

mapper.insert:
INSERT INTO org_department(
