(
#{entity.id}, #{entity.company.id}, #{entity.code}, #{entity.parent.id}, #{entity.ids}, #{entity.name}, #{entity.sort}, #{entity.director.id}, #{entity.manager.id}, #{entity.remarks}, #{entity.createBy}, #{entity.createDate}, #{entity.updateBy}, #{entity.updateDate}, #{entity.delFlag},
)

mapper.update:
UPDATE org_department



      when id=#{entity.id} then #{entity.company.id}




